import React, { useState } from 'react'
import * as Yup from 'yup';
import { useHistory } from 'react-router'
import axios from 'axios'
import { Icon } from '@iconify/react';
import { useFormik, Form, FormikProvider } from 'formik';
import eyeFill from '@iconify/icons-eva/eye-fill';
import eyeOffFill from '@iconify/icons-eva/eye-off-fill';
import { useNavigate } from 'react-router-dom';
// material
import { Stack, TextField, IconButton, InputAdornment } from '@mui/material';
import { LoadingButton } from '@mui/lab';

export default function UserJoin(){
    // const navigate = useNavigate();
    const [showPassword, setShowPassword] = useState(false);

    const history=useHistory()

    const SERVER='http://127.0.0.1:8000/api/user/join'

    const[user,setUser]=useState({
      user_email:'admin@naver.com',
      username:'admin',
      password:'admin',
      user_name:'admin001',
      user_birthday:'2021-06-28',
      user_sex:'male',
      user_phone:'01012345678',
      user_address:'서울특별시 강남구',
      user_vaccinated:'Yes',
      vaccine_type:'Moderna'
    })

    const{user_email, username, password, 
      user_name, user_birthday, user_sex, user_phone, 
      user_address, user_vaccinated, vaccine_type}='user'

    const userJoin=joinRequest=>axios.post(`${SERVER}/??`, JSON.stringify(joinRequest),{headers})

    const headers = {}

    const handleSubmit=e=>{
      e.prventDefault();
      alert(`기입한 회원정보: ${JSON.stringify(user)}`)
      userJoin({user})
      .then(res=>{alert(`가입 완료: ${res.data.result}`)})
      .catch(err=>{alert(`가입 실패: ${err}`)})
    }

    const handleChange =e=>{
      e.preventDefault()
      const{value,name}=e.target
      setUser({...user,[name]:value})    
    }

    const RegisterSchema = Yup.object().shape({
      user_email: Yup.string().email('메일 형식에 맞게 입력해주세요').required('메일 주소를 입력해주세요'),
        password: Yup.string().required('비밀번호 12자 이내로 작성해주세요'),  
      username: Yup.string().required('아이디 12자 이내로 작성해주세요'),
      user_birthday: Yup.string().required(),
      user_sex: Yup.string().required(),
      user_phone: Yup.string().required('핸드폰 연락처 11자리를 입력해주세요'),
      user_address: Yup.string().required(),
      user_vaccinated: Yup.string('백신 접종완료 여부를 알려주세요'),
      vaccine_type: Yup.string()        
      })
      const formik = useFormik({
        initialValues: {
          user_email: '',
          password: '',
          username: '',
          user_birthday: '',
          user_sex:'',
          user_phone:'',
          user_address:'',
          user_vaccinated:'',
          vaccine_type:''
        },
        validationSchema: RegisterSchema,
        onSubmit: () => {
          // navigate('/', { replace: true });
          // navigate=클릭하면 메인페이지 뜨게 주소 넣기
                      // RETRY
        }
      })
      // const { errors, touched, handleSubmit, isSubmitting, getFieldProps } = formik;
      const { errors, touched, isSubmitting, getFieldProps } = formik;

      return (
        <FormikProvider value={formik}>
          <Form autoComplete="off" noValidate onSubmit={handleSubmit}>
            <Stack spacing={3}>
              <Stack direction={{ xs: 'column', sm: 'row' }} spacing={2}>
                <TextField
                  fullWidth
                  label="이름"
                  {...getFieldProps('firstName')}
                  error={Boolean(touched.firstName && errors.firstName)}
                  helperText={touched.firstName && errors.firstName}
                />

                <TextField
                  fullWidth
                  label="성"
                  {...getFieldProps('lastName')}
                  error={Boolean(touched.lastName && errors.lastName)}
                  helperText={touched.lastName && errors.lastName}
                />
              </Stack>

              <TextField
                fullWidth
                autoComplete="username"
                type="email"
                label="메일주소"
                {...getFieldProps('email')}
                error={Boolean(touched.email && errors.email)}
                helperText={touched.email && errors.email}
              />

              <TextField
                fullWidth
                autoComplete="current-password"
                type={showPassword ? 'text' : 'password'}
                label="비밀번호"
                {...getFieldProps('password')}
                InputProps={{
                  endAdornment: (
                    <InputAdornment position="end">
                      <IconButton edge="end" onClick={() => setShowPassword((prev) => !prev)}>
                        <Icon icon={showPassword ? eyeFill : eyeOffFill} />
                      </IconButton>
                    </InputAdornment>
                  )
                }}
                error={Boolean(touched.password && errors.password)}
                helperText={touched.password && errors.password}
              />

              <LoadingButton
                fullWidth
                size="large"
                type="submit"
                variant="contained"
                loading={isSubmitting}
              >
                가입 신청
              </LoadingButton>
            </Stack>
          </Form>
        </FormikProvider>

    )
}
